const rawText = $input.all()[0].json.text;
const cleanedText = rawText.replace(/```json|```/g, '').trim();

let posts;
try {
  posts = JSON.parse(cleanedText);
} catch (error) {
  const arrayMatch = cleanedText.match(/\[.*\]/s);
  if (arrayMatch) {
    posts = JSON.parse(arrayMatch[0]);
  } else {
    throw new Error('No se pudo parsear el output del LLM: ' + cleanedText);
  }
}

return posts.map(postText => ({
  json: {
    author: "urn:li:person:TUID",
    lifecycleState: "PUBLISHED",
    specificContent: {
      "com.linkedin.ugc.ShareContent": {
        shareCommentary: {
          text: postText
        },
        shareMediaCategory: "NONE"
      }
    },
    visibility: {
      "com.linkedin.ugc.MemberNetworkVisibility": "PUBLIC"
    }
  }
}));
